INSERT INTO `detail_company` (`gioi_thieu_cong_ty`, `tuyen_dung`, `iframe`, `showroom`, `id`) VALUES
('<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">I. Giới thiệu về Điện m&aacute;y Đức Thắng</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">1.Giới thiệu chung về C&ocirc;ng ty</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">Điện m&aacute;y Đức Thắng tự h&agrave;o l&agrave; một trong những c&ocirc;ng ty đi ti&ecirc;n phong trong lĩnh vực Thương mại điện tử tại Vi&ecirc;t Nam từ năm 2005. Với uy t&iacute;n v&agrave; kinh nghiệm l&acirc;u năm, dịch vụ chuy&ecirc;n nghiệp gi&uacute;p kh&aacute;ch h&agrave;ng tr&ecirc;n to&agrave;n l&atilde;nh thổ Việt Nam dễ d&agrave;ng mua sắm c&aacute;c sản phẩm trực tuyến.</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">Với mục ti&ecirc;u đem đến cho kh&aacute;ch h&agrave;ng những sản phẩm tuyệt vời c&ugrave;ng dịch vụ mua sắm trực tuyến tin cậy. Điện m&aacute;y Đức Thắng kh&ocirc;ng ngừng n&acirc;ng cao chất lượng dịch vụ v&agrave; lắng nghe &yacute; kiến đ&oacute;ng g&oacute;p từ ph&iacute;a kh&aacute;ch h&agrave;ng để ho&agrave;n thiện dịch vụ một c&aacute;ch tốt nhất.</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">L&agrave; doanh nghiệp chuy&ecirc;n ph&acirc;n phối c&aacute;c sản phẩm điện m&aacute;y, c&ocirc;ng cụ cầm tay; Hiện c&ocirc;ng ty đang l&agrave;m đại l&yacute; ph&acirc;n phối sản phẩm điện m&aacute;y của c&aacute;c h&atilde;ng sản xuất h&agrave;ng đầu thế giới như: Yamaha, Makita, Honda, Stilh... C&ocirc;ng ty c&oacute; trụ sở đặt tại Trung t&acirc;m th&agrave;nh phố H&agrave; Nội v&agrave; mạng lưới ph&acirc;n phối to&agrave;n miền Bắc. Sản phẩm, dịch vụ của C&ocirc;ng ty lu&ocirc;n được kh&aacute;ch h&agrave;ng, thị trường tin tưởng đ&oacute;n nhận v&agrave; đ&aacute;nh gi&aacute; cao về chất lượng v&agrave; uy t&iacute;n phục vụ. Đến nay, sau hơn 10 năm hoạt động, C&ocirc;ng ty thực sự đ&atilde; trở th&agrave;nh nh&agrave; ph&acirc;n phối h&agrave;ng điện m&aacute;y được c&aacute;c đại l&yacute; đặt niềm tin x&acirc;y dựng mối quan hệ đối t&aacute;c chiến lược d&agrave;i l&acirc;u; v&agrave; được kh&aacute;ch h&agrave;ng tin tưởng ủng hộ.</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">2. Chiến lược hoạt động</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">- Tầm nh&igrave;n:</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">X&acirc;y dựng Điện m&aacute;y Đức Thắng trở th&agrave;nh nh&agrave; ph&acirc;n phối h&agrave;ng điện m&aacute;y số 1 tại thị trường miền Bắc; c&oacute; đại l&yacute; v&agrave; sự hiện diện thương hiệu ở tất cả tỉnh thuộc địa b&agrave;n mục ti&ecirc;u.</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">Nghi&ecirc;n cứu ph&aacute;t triển để sản xuất, lắp r&aacute;p một số loại sản phẩm ph&ugrave; hợp với thị trường v&agrave; khả năng của c&ocirc;ng ty mang thương hiệu Việt.</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">Quản trị điều h&agrave;nh hoạt động c&ocirc;ng ty bằng hệ thống quản trị kh&ocirc;ng phụ thuộc vai tr&ograve; c&aacute; nh&acirc;n; hướng đến v&agrave; thực hiện minh bạch hoạt động kinh doanh.</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">X&acirc;y dựng văn h&oacute;a c&ocirc;ng ty hướng tới con người: kh&aacute;ch h&agrave;ng, đối t&aacute;c nh&acirc;n vi&ecirc;n v&agrave; tham gia đ&oacute;ng g&oacute;p cộng đồng; x&acirc;y dựng đội ngũ nh&acirc;n sự chuy&ecirc;n nghiệp, l&agrave;m cho mỗi nh&acirc;n vi&ecirc;n tự h&agrave;o l&agrave; th&agrave;nh vi&ecirc;n của Điện m&aacute;y Đức Thắng.</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">- Sứ mệnh:</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">Mang đến cho người lao động những c&ocirc;ng cụ tốt về t&iacute;nh năng, an to&agrave;n khi sử dụng, c&oacute; độ bền cao v&agrave; gi&aacute; cả hợp l&yacute;, g&oacute;p phần n&acirc;ng cao hiệu quả kinh tế cho kh&aacute;ch h&agrave;ng.</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">- Gi&aacute; trị cốt l&otilde;i:</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">Trở th&agrave;nh biểu tượng h&agrave;ng đầu Việt Nam về cung cấp c&aacute;c sản phẩm h&agrave;ng điện m&aacute;y ch&iacute;nh h&atilde;ng c&oacute; dịch vụ hậu m&atilde;i ho&agrave;n hảo.</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">Li&ecirc;m ch&iacute;nh: ch&iacute;nh trực v&agrave; trung thực trong giao dịch, hợp t&aacute;c.</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">Nguy&ecirc;n tắc ứng xử: minh bạch trong c&ocirc;ng việc; t&ocirc;n trọng trong ứng xử, c&ocirc;ng bằng trong xử thế.</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">Đạo đức: giữ chữ t&iacute;n v&agrave; t&ocirc;n trọng c&aacute;c chuẩn mực đạo đức kinh doanh.</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">T&iacute;nh tu&acirc;n thủ: tu&acirc;n thủ luật ph&aacute;p; ch&iacute;nh s&aacute;ch qui định của c&ocirc;ng ty.</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">II. Về sản phẩm v&agrave; dịch vụ</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">- Tổng quan về sản phẩm dịch vụ:</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">Chuy&ecirc;n cung cấp c&aacute;c thiết bị, m&aacute;y m&oacute;c phục vụ cho ng&agrave;nh x&acirc;y dựng, ng&agrave;nh mộc, ng&agrave;nh cơ kh&iacute; v&agrave; n&ocirc;ng-l&acirc;m nghiệp, bao gồm c&aacute;c dụng cụ điện cầm tay&nbsp; Makita, Maktec, Stilh, Honda, Echo, Yamaha&hellip;</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">C&aacute;c loại M&aacute;y đục ph&aacute; b&ecirc; t&ocirc;ng, M&aacute;y khoan, M&aacute;y m&agrave;i, M&aacute;y cắt, M&aacute;y cưa, M&aacute;y b&agrave;o.</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">M&aacute;y cưa x&iacute;ch, m&aacute;y cắt cỏ, đầu nổ, m&aacute;y ph&aacute;t điện, dụng cụ ngo&agrave;i trời.</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">Phụ kiện, Linh kiện k&egrave;m theo M&aacute;y : Đĩa m&agrave;i, Đĩa cắt, Mũi khoan, Mũi đục&hellip;</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">Cung cấp dịch vụ: Bảo h&agrave;nh - Bảo tr&igrave; , thay thế linh kiện sản phẩm.</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">- Những lợi &iacute;ch khi mua sắm trực tuyến tại dienmaythanhloi.vn:</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">Được tư vấn trực tiếp để lựa chọn sản phẩm tốt nhất.</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">Giao h&agrave;ng v&agrave; thu tiền tại nh&agrave; tr&ecirc;n to&agrave;n quốc! Miễn ph&iacute; vận chuyển tại Nội th&agrave;nh H&agrave; Nội (Với đơn h&agrave;ng tr&ecirc;n 1.000.000đ).</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">Dịch vụ chăm s&oacute;c, hỗ trợ kh&aacute;ch h&agrave;ng tận t&igrave;nh trong suốt qu&aacute; tr&igrave;nh mua h&agrave;ng, từ 8h00 đến 20h00.</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">Cập nhật nhiều chương tr&igrave;nh Ưu đ&atilde;i, Khuyến mại giảm gi&aacute;, Tặng qu&agrave;.</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">H&atilde;y truy cập dienmaythanhloi.vn để được tư vấn tốt nhất.</span></div>\r\n<div class=\"panel-about-title\"><span style=\"font-size: 10pt;\">&nbsp;</span></div>', '', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3724.187961974414!2d105.90575791533212!3d21.02516389327604!2m3!1f0!2f0!3f0!3m2!1i1024!2i76